parameters.functionCode:
// Transform Shopify orders for OmniTrackIQ
const items = [];

for (const item of $input.all()) {
  const order = item.json;

  // Extract UTM params from landing_site or referring_site
  let source = 'direct';
  let campaign = null;

  if (order.landing_site) {
    try {
      const url = new URL('https://example.com' + order.landing_site);
      source = url.searchParams.get('utm_source') || 'direct';
      campaign = url.searchParams.get('utm_campaign');
    } catch (e) {}
  }

  // Map source to platform
  let platform = 'organic';
  if (source.includes('facebook') || source.includes('fb') || source.includes('instagram')) {
    platform = 'facebook';
  } else if (source.includes('google')) {
    platform = 'google_ads';
  } else if (source.includes('tiktok')) {
    platform = 'tiktok';
  }

  items.push({
    json: {
      account_id: $env.OMNITRACKIQ_ACCOUNT_ID,
      shopify_order_id: String(order.id),
      order_number: order.order_number,
      order_date: order.created_at.split('T')[0],
      revenue: parseFloat(order.total_price) || 0,
      subtotal: parseFloat(order.subtotal_price) || 0,
      discount: parseFloat(order.total_discounts) || 0,
      currency: order.currency || 'USD',
      status: order.financial_status || 'pending',
      fulfillment_status: order.fulfillment_status || 'unfulfilled',
      customer_email: order.customer?.email || null,
      items_count: order.line_items?.length || 0,
      source: source,
      platform: platform,
      campaign: campaign,
      discount_codes: order.discount_codes?.map(d => d.code).join(',') || null
    }
  });
}

return items;

